import React from 'react';
import ReactDOM from 'react-dom';

const Header = ({ course }) => <h1>{course}</h1>

const Part = ({ id, part, exercises }) => <p key={id}>{part} {exercises}</p>

const Content = ({ parts }) => {
    const listParts = () => parts.map(part => <Part id={part.id} part={part.name} exercises={part.exercises} />)
    const total = parts.reduce((totalEx, partIter) => {
        console.log('inside total', totalEx, partIter)
        return totalEx + partIter.exercises
    }, 0)

    return (
        <div>
            {listParts()}
            <strong>total of {total} exercises</strong>
        </div>
    )
}

const Course = ({ course }) => (
    <div>
        <Header course={course.name} />
        <Content parts={course.parts} />
    </div>
)

const App = () => {
    const course = {
        name: 'Half Stack application development',
        parts: [
            {
                name: 'Fundamentals of React',
                exercises: 10,
                id: 1
            },
            {
                name: 'Using props to pass data',
                exercises: 7,
                id: 2
            },
            {
                name: 'State of a component',
                exercises: 14,
                id: 3
            }
        ]
    }

    return (
        <div>
            <Course course={course} />
        </div>
    )
}

ReactDOM.render(<App />, document.getElementById('root'));

